const fs = require('fs');
const path = require('path');

function readAndDecodeSync(format) {
  const decoder = require(`./${format}`);
  const filePath = path.join(__dirname, `${format}.dat`);
  const content = fs.readFileSync(filePath, 'utf8');
  return decoder.decode(content);
}

exports.readAndDecodeSync = readAndDecodeSync;